import {Octokit} from "@octokit/core";
import React from 'react';
import Search from '../../library/Search';

export default class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            repos: [],
            query: '',
            description: '',
        }
    }

    octokit = new Octokit({
        auth: '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',
    });

    setRepos = (repos) => {
        console.log('repos');
        console.log(repos);
        this.setState({repos});
    }

    setQuery = (query) => {
        const {octokit, setRepos} = this;
        this.setState({query});

        query !== '' ?
            octokit.request('GET /search/repositories', {
                q: query,
                per_page: 20,
            }).then((response) => setRepos(response.data.items))
            :
            setRepos([]);
    }

    setDescription = (description) => this.setState({description});

    render() {
        const {setQuery, setDescription} = this;
        const {query} = this.state;
        const repos = JSON.parse(localStorage.getItem('repos'));
        return (
            <div className="app-container">
                <div className="content-container">
                    <Search
                        value={query}
                        onChange={setQuery}
                        options={repos}
                    />
                    {repos.map((repo, key) =>
                        <div
                            className="repo"
                            key={key}
                            onClick={() => setDescription(repo.name)}
                        >
                            {repo.name}
                        </div>
                    )}
                </div>
                <div className="content-container">
                    <h1>Description</h1>
                </div>
            </div>
        );
    }
}
