        <div className="search-wrapper">
            <input
                value={query}
                placeholder={"Find a Repository"}
                onChange={(e) => setQuery(e.target.value)}
                onFocus={() => setFocused(true)}
            />
            {focused &&
            <div className="dropdown">
                {options.map((option) =>
                    <div
                        className="option"
                        key={option.id}
                        // onClick={() => addOption(option)}
                    >
                        {option.name}
                    </div>
                )}
            </div>
            }
        </div>
    );
}

