import {Octokit} from "@octokit/core";
import React, {Component} from 'react';
import Search from '../../library/Search';
import Repo from '../../library/Repo';

export default class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            // results: [],
            repos: JSON.parse(localStorage.getItem('repos')),
            // query: '',
            description: '',
        }
    }

    // octokit = new Octokit({
    //     auth: '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',
    // });

    setResults = (results) => this.setState({results});

    // setQuery = (query) => {
    //     const {octokit, setResults} = this;
    //     this.setState({query});
    //
    //     query !== '' ?
    //         octokit.request('GET /search/repositories', {
    //             q: query,
    //             per_page: 20,
    //         }).then((response) => setResults(response.data.items))
    //         :
    //         setResults([]);
    // }

    setDescription = (description) => this.setState({description});

    removeRepo = (index) => {
        const {repos} = this.state;
        repos.splice(index, 1);
        localStorage.setItem('repos', JSON.stringify(repos));
        this.setState({repos});
        console.log(index);
    }

    render() {
        const {setQuery, setDescription, removeRepo} = this;
        const {description, results, query} = this.state;
        const storedRepos = JSON.parse(localStorage.getItem('repos'));
        return (
            <div className="app-container">
                <div className="content-container">
                    <Search
                        // selectOption={}
                        // value={query}
                        // onChange={setQuery}
                        // options={results}
                    />
                    <div className="scroll">
                        {storedRepos.map((repo, key) =>
                            <Repo
                                repo={repo}
                                setDescription={setDescription}
                                removeRepo={() => removeRepo(key)}
                                key={key}
                            />
                        )}
                    </div>
                </div>
                <div className="content-container">
                    <h1
                        className="gradient"
                    >
                        Description
                    </h1>
                    {description}
                </div>
            </div>
        );
    }
}
