import types from '../types';

export const setQueryStart = (state) => ({
    ...state,
    loaders: {
        ...state.loaders,
        queryLoading: true,
    },
});

export const setQuerySuccess = (state, {success}) => ({
    ...state,
    loaders: {
        ...state.loaders,
        queryLoading: false,
    },
    success,
});

export const setQueryFail = (state, {error}) => ({
    ...state,
    loaders: {
        ...state.loaders,
        queryLoading: false,
    },
    error,
});

export default {
    [types.SET_QUERY_START]: setQueryStart,
    [types.SET_QUERY_SUCCESS]: setQuerySuccess,
    [types.SET_QUERY_FAIL]: setQueryFail,
};
