import {Octokit} from '@octokit/core';
import React, {useState} from 'react';
import onClickOutside from 'react-onclickoutside'
import Loader from '../Loader';

function Search() {
    const [query, setQuery] = useState('');
    const [results, setResults] = useState([]);
    const [focused, setFocused] = useState(false);
    Search.handleClickOutside = () => setFocused(false);
    const onChange = (query) => {
        setQuery(query);
        const octokit = new Octokit({auth: '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'});
        query !== '' ?
            octokit.request('GET /search/repositories', {
                q: query,
                per_page: 20,
            }).then((response) => setResults(response.data.items))
            :
            setResults([]);
    }
    const onClick = (result) => {
        setFocused(false);
    }
    return (
        <div className="search-wrapper">
            <Loader/>

            <input
                value={query}
                placeholder={"Find a Repository"}
                onChange={(e) => onChange(e.target.value)}
                onFocus={() => setFocused(true)}
            />
            {focused && query !== '' &&
            <div className="dropdown">
                {/*{results.map((result) =>*/}
                {/*    <div*/}
                {/*        className="option"*/}
                {/*        key={result.id}*/}
                {/*        onClick={() => onClick(result)}*/}
                {/*    >*/}
                {/*        {result.name}*/}
                {/*    </div>*/}
                {/*)}*/}
            </div>
            }
        </div>
    );
}

const clickOutsideConfig = {handleClickOutside: () => Search.handleClickOutside};

export default onClickOutside(Search, clickOutsideConfig);
