import {Octokit} from "@octokit/core";
import React from 'react';
import Search from '../../library/Search';

export default class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            repos: [],
            query: '',
        }
    }

    octokit = new Octokit({
        auth: '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',
    });

    setRepos = (repos) => {
        console.log('repos');
        console.log(repos);
        this.setState({repos});
    }

    setQuery = (query) => {
        const {octokit, setRepos} = this;
        this.setState({query});

        octokit.request('GET /search/repositories', {
            q: query,
            per_page: 20,
        }).then(
            (response) => {
                setRepos(response.data.items);
            }
        );
    }

    render() {
        const {setQuery} = this;
        const {query, repos} = this.state;
        return (
            <div className="app-container">
                <div className="flex-1">
                    <Search
                        value={query}
                        onChange={setQuery}
                        options={repos}
                    />
                </div>
                <div className="flex-1">
                    Description
                </div>
            </div>
        );
    }
}
