import {Octokit} from '@octokit/core';
import {call, put, takeLatest} from "redux-saga/effects";
import {getResultsFail, getResultsStart, getResultsSuccess} from '../actions/get-results';
import types from '../types';

export const httpCall = ({owner, repo}) => {
    const octokit = new Octokit({auth: process.env.REACT_APP_AUTHORIZATION});
    return octokit.request('GET /repos/{owner}/{repo}/releases/latest', {
        owner,
        repo,
    }).then((response) => {
        return response.data.items;
    }).catch((err) => console.log(`Error retrieving release notes for '${repo}'\n\n${err}`));
}

function* getLatestRelease({owner, repo}) {
    try {
        yield put(getResultsStart());

        let results = yield call(httpCall, {owner, repo});

        yield put(getResultsSuccess(results));
    } catch (err) {
        yield put(getResultsFail(err));
    }
}

export default function* getLatestReleaseSaga() {
    yield takeLatest(types.GET_LATEST_RELEASE, getLatestRelease);
}
