import getLatestReleaseReducer from './reducer/get-latest-release';
import getResultsReducer from './reducer/get-results';
import refreshPage from './reducer/refresh-page';
import types from './types';

const initialState = {
    loaders: {
        refreshingPage: false,
        releaseLoading: false,
        queryLoading: false,
    },
    newReleases: [
        {
            "url": "https://api.github.com/repos/twbs/bootstrap-sass/releases/15585623",
            "assets_url": "https://api.github.com/repos/twbs/bootstrap-sass/releases/15585623/assets",
            "upload_url": "https://uploads.github.com/repos/twbs/bootstrap-sass/releases/15585623/assets{?name,label}",
            "html_url": "https://github.com/twbs/bootstrap-sass/releases/tag/v3.4.1",
            "id": 15585623,
            "author": {
                "login": "MartijnCuppens",
                "id": 11559216,
                "node_id": "MDQ6VXNlcjExNTU5MjE2",
                "avatar_url": "https://avatars.githubusercontent.com/u/11559216?v=4",
                "gravatar_id": "",
                "url": "https://api.github.com/users/MartijnCuppens",
                "html_url": "https://github.com/MartijnCuppens",
                "followers_url": "https://api.github.com/users/MartijnCuppens/followers",
                "following_url": "https://api.github.com/users/MartijnCuppens/following{/other_user}",
                "gists_url": "https://api.github.com/users/MartijnCuppens/gists{/gist_id}",
                "starred_url": "https://api.github.com/users/MartijnCuppens/starred{/owner}{/repo}",
                "subscriptions_url": "https://api.github.com/users/MartijnCuppens/subscriptions",
                "organizations_url": "https://api.github.com/users/MartijnCuppens/orgs",
                "repos_url": "https://api.github.com/users/MartijnCuppens/repos",
                "events_url": "https://api.github.com/users/MartijnCuppens/events{/privacy}",
                "received_events_url": "https://api.github.com/users/MartijnCuppens/received_events",
                "type": "User",
                "site_admin": false
            },
            "node_id": "MDc6UmVsZWFzZTE1NTg1NjIz",
            "tag_name": "v3.4.1",
            "target_commitish": "master",
            "name": "v3.4.1",
            "draft": false,
            "prerelease": false,
            "created_at": "2019-02-13T18:59:05Z",
            "published_at": "2019-02-15T14:58:11Z",
            "assets": [],
            "tarball_url": "https://api.github.com/repos/twbs/bootstrap-sass/tarball/v3.4.1",
            "zipball_url": "https://api.github.com/repos/twbs/bootstrap-sass/zipball/v3.4.1",
            "body": "* **Security:** Fixed an XSS vulnerability (CVE-2019-8331) in our tooltip and popover plugins by implementing a new HTML sanitizer\r\n* Handle bad selectors (`#`) in `data-target` for Dropdowns\r\n* Clarified tooltip selector documentation\r\n* Added support for NuGet contentFiles"
        }
    ],
    releaseNotes: null,
    repos: JSON.parse(localStorage.getItem('repos')) ?? [],
    results: [],
    query: '',
};

export const addRepo = (state, {repo}) => {
    const repos = [...state.repos, {data: repo}];
    localStorage.setItem('repos', JSON.stringify(repos));
    return ({
        ...state,
        repos,
    });
};

export const removeRepo = (state, {index}) => {
    const repos = [...state.repos.slice(0, index), ...state.repos.slice(index + 1)];
    localStorage.setItem('repos', JSON.stringify(repos));
    return ({
        ...state,
        repos,
    });
};

export const setReleaseNotes = (state, {index}) => ({
    ...state,
    releaseNotes: (state.repos[index].notes?.body ?? 'Release Notes Unavailable')
});

export const setQuery = (state, {query}) => ({...state, query});

export const updateReleaseNotes = (state, {index}) => {
    let repos = state.repos;
    let newReleases = state.newReleases;

    delete newReleases[index];
    localStorage.setItem('repos', JSON.stringify(repos));
    return ({
        ...state,
        newReleases,
    });
}

const reducer = {
    [types.ADD_REPO]: addRepo,
    [types.REMOVE_REPO]: removeRepo,
    [types.SET_RELEASE_NOTES]: setReleaseNotes,
    [types.SET_QUERY]: setQuery,
    [types.UPDATE_RELEASE_NOTES]: updateReleaseNotes,
    ...getLatestReleaseReducer,
    ...getResultsReducer,
    ...refreshPage,
};

export default (state = initialState, action = {}) => reducer[action.type] ? reducer[action.type](state, action) : state;
