import React from 'react';
import Close from '../../assets/img/close.svg'

function Repo({repo, setDescription, removeRepo, key}) {
    return (
        <div
            className="repo"
        >
            <div
                className="title cursor"
                onClick={() => setDescription(repo.description)}
            >
                {repo.name}
            </div>
            <img
                className="close cursor"
                src={Close}
                onClick={() => removeRepo(key)}
                alt="X"
            />
        </div>
    );
}

export default Repo;
