import React, {Component} from 'react';
import Repo from '../../library/Repo';
import Search from '../../library/Search';

export default class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            repos: JSON.parse(localStorage.getItem('repos')),
        }
    }

    setResults = (results) => this.setState({results});

    setDescription = (description) => this.setState({description});

    removeRepo = (index) => {
        const {repos} = this.state;
        repos.splice(index, 1);
        localStorage.setItem('repos', JSON.stringify(repos));
        this.setState({repos});
        console.log(index);
    }

    render() {
        const {setDescription, removeRepo} = this;
        const {description} = this.state;
        const storedRepos = JSON.parse(localStorage.getItem('repos'));
        return (
            <div className="app-container">
                <div className="content-container">
                    <Search
                        // selectOption={}
                        // value={query}
                        // onChange={setQuery}
                        // options={results}
                    />
                    <div className="scroll">
                        {storedRepos.map((repo, key) =>
                            <Repo
                                repo={repo}
                                setDescription={setDescription}
                                removeRepo={() => removeRepo(key)}
                                key={key}
                            />
                        )}
                    </div>
                </div>
                <div className="content-container">
                    <h1
                        className="gradient"
                    >
                        Description
                    </h1>
                    {description}
                </div>
            </div>
        );
    }
}
