import React from 'react';
import Close from '../../assets/img/close.svg'

function Repo({repo, setDescription, removeRepo}) {
    return (
        <div
            className="repo"
            onClick={() => setDescription(repo.description)}
        >
            <h3>{repo.name}</h3>
            <div
                onClick={removeRepo}
            >
                <img
                    src={Close}
                    alt="X"
                />
            </div>
        </div>
    );
}

export default Repo;
