const initialState = {
    age: 20,
    loaders: {
        queryLoading: false,
    },
};

const reducer = (state = initialState, action) => {
    const newState = { ...state };

    switch (action.type) {
        case "AGE_UP_ASYNC":
            newState.age += action.value;
            break;

        case "AGE_DOWN":
            newState.age -= action.value;
            break;
    }
    return newState;
};

export default (state = initialState, action = {}) => reducer[action.type] ? reducer[action.type](state, action) : state;
