import setQueryReducer from './reducer/set-query';

const initialState = {
    loaders: {
        queryLoading: false,
    },
    releaseNotes: '',
    repos: JSON.parse(localStorage.getItem('repos')),
    results: [],
    query: '',
};

export const setQuery = (state) => ({
    ...state,
    loaders: {
        ...state.loaders,
        isUpdatingActionItem: false,
    },
});

const reducer = {
    ...setQueryReducer,
};

export default (state = initialState, action = {}) => reducer[action.type] ? reducer[action.type](state, action) : state;
